// pages/stock/StockListPage.jsx
import { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import SearchComponent from "../../components/SearchComponent";
import { BASE_URL } from "../../api/config";

export default function StockListPage() {
  const [sp, setSp] = useSearchParams();
  const [name, setName] = useState(sp.get("name") ?? "");
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(false);

  const fetchList = async (keyword = "") => {
    setLoading(true);
    try {
      const { data } = await axios.get(`${BASE_URL}/stocks`, { params: keyword ? { name: keyword } : {} });
      setRows(Array.isArray(data) ? data : data?.content ?? []);
    } finally {
      setLoading(false);
    }
  };

  const handleDebounced = (debounced) => {
    const next = new URLSearchParams(sp);
    if (debounced) next.set("name", debounced);
    else next.delete("name");
    setSp(next, { replace: true });
    fetchList(debounced);
  };

  useEffect(() => { fetchList(sp.get("name") ?? ""); }, []); // 최초 1회

  return (
    <div className="p-4">
      <div className="flex items-center gap-2 mb-4">
        <SearchComponent
          value={name}
          onChange={setName}
          onDebounced={handleDebounced}
          delay={300}
          minLength={0}
          placeholder="검색"
        />
        <button className="px-3 py-2 border rounded" onClick={() => setName("")}>초기화</button>
      </div>

      {loading ? (
        <div>불러오는 중...</div>
      ) : (
        <table className="w-full border-collapse">
          <thead>
            <tr>
              <th className="border p-2">상품명</th>
              <th className="border p-2">현재재고</th>
              <th className="border p-2">누적입고</th>
              <th className="border p-2">누적출고</th>
            </tr>
          </thead>
          <tbody>
            {rows.length ? rows.map((r) => (
              <tr key={r.id ?? r.pid ?? r.productId}>
                <td className="border p-2">{r.pname ?? r.productName}</td>
                <td className="border p-2 text-right">{Number(r.currentStock ?? r.qty ?? 0).toLocaleString()}</td>
                <td className="border p-2 text-right">{Number(r.totalIn ?? 0).toLocaleString()}</td>
                <td className="border p-2 text-right">{Number(r.totalOut ?? 0).toLocaleString()}</td>
              </tr>
            )) : (
              <tr><td className="border p-4 text-center" colSpan={4}>검색 결과 없음</td></tr>
            )}
          </tbody>
        </table>
      )}
    </div>
  );
}
