import WoodenMainLogo from "../../WoodenMainLogo.png";
import { Link } from "react-router-dom";
import { NavBar } from "../../arrays/MainArrays";

const HeaderLayout = ( {
// GuideMenuLayout
children,
//--------
currentNavID,
isLoggedIn,
setIsLoggedIn,
handleNavClick,
navigate,
navigateAndReset,
setOpenSideSet,
} ) => {
  return(
    <>
      <header>
            <div className="logo">
                    <h1>
                        <Link to = "/">
                            <img src={WoodenMainLogo} alt="MainLogo" className="mainLogo" />
                        </Link>
                    </h1>
                </div>
                <nav>
                    <ul className="header-ul">
                        { NavBar.map(data => (
                            <li key={data.id} 
                            className={currentNavID === data.id ? "selected":""}
                            onClick={() => {;
                                if (isLoggedIn){
                                    handleNavClick(data);
                                    navigate(data.path)
                                }
                                else{ alert("로그인이 필요합니다.");
                                    navigate("/login")
                                }
                            }
                            }  
                            >
                            <Link to={data.path} className="nav-items">{data.name}</Link>
                            </li>
                        ))}
                        
                    </ul>
                    {/* 수정 10-02 */}
                    <div className="side-tab">
                        <p className="loginbtn">    {/*테스트*/}
                        {isLoggedIn ? 
                            <span
                            onClick={() => {
                            localStorage.removeItem("isLoggedIn");
                            setIsLoggedIn(false);
                            alert("로그아웃 되었습니다.")
                            navigateAndReset("/");
                            }}
                            >
                                로그아웃
                            </span>
                        : 
                            <span onClick={() => navigateAndReset("/login")}>로그인</span>
                        }
                        </p>
                        <Link onClick = { (e)=> {setOpenSideSet(true); e.preventDefault()}}className="hamburger">
                            <span>{/*햄버거메뉴*/}</span>
                            <span>{/*햄버거메뉴*/}</span>
                            <span>{/*햄버거메뉴*/}</span>
                        </Link>
                </div> 
            </nav>
            <>
              {children}
            </>
        </header>
    </>
  );
}
export default HeaderLayout;
