// src/pages/order/OrderListPage.jsx
import { useEffect } from "react";
import {
  getOrderList,
  createOrderList,
  updateOrderList,
  deleteOrderList,
} from "../../api/OrderListAPI";
import { useCRUD } from "../../hook/useCRUD";
import ModalComponent from "../../components/ModalComponent";
import OrderListForm from "../../form/order/OrderListForm";
import ButtonComponent from "../../components/ButtonComponent";
import BackButtonComponent from "../../components/BackButtonComponent";
import { initForms } from "../../arrays/TableArrays";
import { useNavigate } from "react-router-dom";
import { OrderListArr } from "../../arrays/OrderListArrays";

const api = {
  getAll: getOrderList,
  create: createOrderList,
  update: updateOrderList,
  delete: deleteOrderList,
};

const OrderListPage = () => {
  const navigate = useNavigate();

  const {
    items: orderList,
    setItems: setOrderList,
    handleChange,
    handleCreate,
    handleUpdate,
    handleDelete,
    openEdit,
    openCreate,
    isCreateOpen,
    isEditOpen,
    closeCreate,
    closeEdit,
    selectedItem,
    customer,
    itemList,
    setCustomer,
    setItemList,
    customerId,
    itemListId,
    setCustomerId,
    setItemListId,
    orderQty,
    setOrderQty,
  } = useCRUD({
    initFormData: () => initForms.orderList,
    api,
    keyField: "orderNo",
  });

  useEffect(() => {
    const fetchData = async () => {
      const data = await getOrderList();
      setOrderList(data);
    };
    fetchData();
  }, []);

  return (
    <div className="page-wrapper">
      <BackButtonComponent
        text="< &nbsp;이전페이지"
        onClick={() => navigate(-1)}
      />
      <h2 style={{ textAlign: "center" }}>상품주문서</h2>

      {/* 테이블 */}
      <table>
        <thead>
          <tr>
            {OrderListArr.map((col) => (
              <th key={col.id}>{col.content}</th>
            ))}
          </tr>
        </thead>

        <tbody>
          {orderList && orderList.length > 0 ? (
            orderList.map((order, index) => (
              <tr key={order.orderNo} className="row" onClick={() => openEdit(order)}>
                <td>{order.orderNo}</td> {/* 주문번호 */}
                <td>{order.orderDate}</td> {/* 주문일자 */}
                <td style={{ color: "blue", textDecoration: "underline" }}>
                  {order.cusComp} {/* 판매처명 */}
                </td>
                <td>{order.itemName}</td> {/* 상품명 */}
                <td>{order.orderQty}</td> {/* 수량 */}
                <td>{order.orderPrice.toLocaleString()} 원</td> {/* 단가 */}
                <td>{order.orderState}</td> {/* 주문상태 */}
                <td>{order.orderDeliState}</td> {/* 납품상태 */}
                <td>{order.deliveryDate}</td> {/* 납품일자 */}
                <td>{order.cusAddr}</td> {/* 주소 */}
                <td>{(order.orderQty * order.orderPrice).toLocaleString()} 원</td> {/* 총 금액 */}
              </tr>
            ))
            ) : (
              <tr>
                <td colSpan={11} style={{ textAlign: "center" }}>
                  데이터가 없습니다.
                </td>
              </tr>
              )}
        </tbody>

      </table>

      {/* 등록 버튼 */}
      <br />
      <ButtonComponent onClick={openCreate} text={"주문 등록"} cln="submit" />

      {/* 등록 모달 */}
      <ModalComponent
        isOpen={isCreateOpen}
        onClose={closeCreate}
        title="상품주문서 등록"
        onConfirm={handleCreate}
      >
        <OrderListForm
          customer={customer}
          setCustomerId={setCustomerId}
          customerId={customerId}
          itemList={itemList}
          setItemListId={setItemListId}
          orderQty={orderQty}
          setOrderQty={setOrderQty}
          handleChange={handleChange}
        >
          <ButtonComponent text={"등록"} onClick={handleCreate} cln="submit" />
        </OrderListForm>
      </ModalComponent>

      {/* 수정/삭제 모달 */}
      <ModalComponent
        isOpen={isEditOpen}
        onClose={closeEdit}
        title="상품주문서 수정/삭제"
        onConfirm={handleUpdate}
      >
        {selectedItem && (
          <OrderListForm
            customer={customer}
            setCustomerId={setCustomerId}
            customerId={customerId}
            itemList={itemList}
            setItemListId={setItemListId}
            orderQty={orderQty}
            setOrderQty={setOrderQty}
            handleChange={handleChange}
          >
            <div className="btn-wrapper">
              <ButtonComponent
                text="수정"
                onClick={handleUpdate}
                cln="fixbtn"
              />
              <ButtonComponent
                text="삭제"
                onClick={handleDelete}
                cln="delbtn"
              />
            </div>
          </OrderListForm>
        )}
      </ModalComponent>
    </div>
  );
};

export default OrderListPage;
