import { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import { getItemStockList } from "../../api/ItemStockAPI.jsx";
import { CompletedPlanArrays, ItemStockColumns, safeCompleted } from "../../arrays/ItemStockArrays.jsx";
import BackButtonComponent from "../../components/BackButtonComponent.jsx";
import ButtonComponent from "../../components/ButtonComponent.jsx";
import SearchComponent from "../../components/SearchComponent.jsx";

const StockListPage = () => {
  const navigate = useNavigate();
  const [rows, setRows] = useState([]);

  // 즉시 입력값과 디바운스된 검색어 분리
  const [q, setQ] = useState("");
  const [term, setTerm] = useState("");

  const reload = async () => {
    try {
      const list = await getItemStockList();
      setRows(Array.isArray(list) ? list : []);
    } catch (e) {
      console.error(e);
      setRows([]);
      alert(e?.response?.data?.error || "재고 목록을 가져오지 못했습니다.");
    }
  };

  useEffect(() => { reload(); }, []);

  const filtered = useMemo(() => {
    const t = term.trim().toLowerCase();
    if (!t) return rows;
    return rows.filter(r => (r.itemName || "").toLowerCase().includes(t));
  }, [rows, term]);

  const totals = useMemo(() => {
    return filtered.reduce(
      (acc, r) => {
        acc.isQty += Number(r.isQty || 0);
        acc.totalIn += Number(r.totalIn || 0);
        acc.totalOut += Number(r.totalOut || 0);
        return acc;
      },
      { isQty: 0, totalIn: 0, totalOut: 0 }
    );
  }, [filtered]);

  const safeCompleted = Array.isArray(completed) ? completed : [];

  return (
    <div className="page-wrapper">
      <BackButtonComponent text="< 이전페이지" onClick={() => navigate(-1)} />
      <h2 style={{ textAlign: "center" }}>현재/누적 재고</h2>

      {/* 상단 툴바 */}
      <div style={{ display: "flex", gap: 12, alignItems: "center", margin: "8px 0" }}>
        <SearchComponent
          value={q}
          onChange={setQ}
          onDebounced={setTerm}
          delay={300}
          placeholder="검색"
          className="border rounded px-3 py-2"
        />
        <ButtonComponent onClick={reload} text="새로고침" cln="submit" />
        <div style={{ marginLeft: "auto", fontWeight: 600 }}>
          현재재고 합: {totals.isQty.toLocaleString()} &nbsp;/&nbsp;
          누적입고: {totals.totalIn.toLocaleString()} &nbsp;/&nbsp;
          누적출고: {totals.totalOut.toLocaleString()}
        </div>
      </div>

      {/* 표 */}
      <table>
        <thead>
          <tr>
            {ItemStockColumns.map((c) => (
              <th key={c.id}>{c.label}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filtered.length ? (
            filtered.map((row) => (
              <tr key={row.isNo} className="row">
                {ItemStockColumns.map((c) => (
                  <td key={`${row.isNo}-${c.id}`} style={{ textAlign: c.align || "left" }}>
                    {typeof row[c.key] === "number"
                      ? Number(row[c.key]).toLocaleString()
                      : (row[c.key] ?? "")}
                  </td>
                ))}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={ItemStockColumns.length} style={{ textAlign: "center" }}>
                데이터가 없습니다.
              </td>
            </tr>
          )}
        </tbody>
      </table>

      <hr className="my-6 opacity-30" />

      <h3 className="text-xl font-semibold mb-2">생산완료 리스트</h3>

      <table className="w-full border-collapse border">
        <thead>
          <tr className="bg-gray-100">
            {CompletedPlanArrays.map(col => (
              <th key={col.clmn} className="border px-2 py-1">{col.content}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {safeCompleted.length ? safeCompleted.map(row => (
            <tr key={row.planNo}>
              {CompletedPlanArrays.map(col => (
                <td key={`${row.planNo}-${col.clmn}`} className="border px-2 py-1">
                  {row[col.clmn] ?? ""}
                </td>
              ))}
            </tr>
          )) : (
            <tr>
              <td className="border px-2 py-6 text-center" colSpan={CompletedPlanArrays.length}>
                생산완료 내역이 없습니다.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default StockListPage;
