import { useEffect, useMemo, useState } from "react";
import { fetchMonthlySalesAll, fetchItemQtyMonthlyByYm } from "../api/salesApi";
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from "recharts";

// YYYY-MM 정규화
function toYYYYMM(input) {
  if (!input) return null;
  const m = String(input).match(/(\d{4})\D?(\d{1,2})/);
  if (!m) return null;
  const y = m[1];
  const mm = String(parseInt(m[2], 10)).padStart(2, "0");
  return `${y}-${mm}`;
}

export default function ItemQtyMonthlyChart({ month: fixedMonth, topN = 10 }) {
  const [monthList, setMonthList] = useState([]); // ["2025-07","2025-08",...]
  const [month, setMonth] = useState(fixedMonth || ""); // 선택 월
  const [rows, setRows] = useState([]); // 선택 월의 TopN
  const [loading, setLoading] = useState(true);
  const [loadingItems, setLoadingItems] = useState(false);
  const [error, setError] = useState("");

  // 1) 월 목록 로드: /api/sales/monthly → ym만 추출
  useEffect(() => {
    (async () => {
      try {
        setError("");
        setLoading(true);
        const data = await fetchMonthlySalesAll(); // [{ ym, total }, ...]
        const months = Array.from(
          new Set(
            (Array.isArray(data) ? data : []).map((r) =>
              toYYYYMM(r?.ym ?? r?.yearMonth ?? r?.YM)
            )
          )
        )
          .filter(Boolean)
          .sort();
        setMonthList(months);
        if (!fixedMonth && months.length) setMonth(months[months.length - 1]); // 최신 달
      } catch (e) {
        setError(e?.message || "월 목록 로드 실패");
      } finally {
        setLoading(false);
      }
    })();
  }, [fixedMonth]);

  // 2) 선택 월의 TopN 로드: /api/sales/item-qty-monthly?ym=YYYY-MM
  useEffect(() => {
    if (!month) return;
    (async () => {
      try {
        setLoadingItems(true);
        setError("");
        const data = await fetchItemQtyMonthlyByYm(month); // [{ itemNo,itemName,qty }, ...]
        setRows(Array.isArray(data) ? data : []);
      } catch (e) {
        setError(e?.message || "월별 품목 수량 로드 실패");
      } finally {
        setLoadingItems(false);
      }
    })();
  }, [month]);

  // 3) 차트 데이터
  const chartData = useMemo(() => {
    return (rows || [])
      .map((r) => ({
        itemName: r.itemName ?? `ITEM-${r.itemNo}`,
        totalQty: Number(r.totalQty ?? r.qty ?? r.quantity ?? 0),
      }))
      .sort((a, b) => b.totalQty - a.totalQty)
      .slice(0, topN);
  }, [rows, topN]);

  if (loading) return <div className="card">월 목록 로딩중...</div>;
  if (error) return <div className="card text-red-500">에러: {error}</div>;
  if (!monthList.length)
    return <div className="card">월 데이터가 없습니다.</div>;

  return (
    <div className="card" style={{ height: "100%" }}>
      <div
        className="card-header"
        style={{ display: "flex", gap: 8, alignItems: "center" }}
      >
        <h3 className="card-title" style={{ marginRight: "auto" }}>
          상품별 월 판매 수량 (Top {topN})
        </h3>

        <div style={{ position: "relative", zIndex: 1000 }}>
          <select
            value={month || ""}
            onChange={(e) => setMonth(e.target.value)}
            className="border rounded px-2 py-1 bg-white text-black"
          >
            {!month && (
              <option value="" disabled>
                월 선택
              </option>
            )}
            {monthList.map((m) => (
              <option key={m} value={m}>
                {m}
              </option>
            ))}
          </select>
        </div>
      </div>

      <div className="chart-body">
        {" "}
        <ResponsiveContainer width="100%" height="100%">
          <BarChart
            key={`${month}-${chartData.length}`} // ⬅️ 추가
            data={chartData}
            margin={{ top: 12, right: 16, left: 70, bottom: 24 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            {/* X축: 항목명 보이게 각도/높이/간격 조정 */}
            <XAxis
              dataKey="itemName"
              interval={0}
              tick={{ fontSize: 12 }}
              angle={-25}
              height={72}
              tickMargin={10}
            />
            {/* Y축: 천단위 표기 + 폭 확보 */}
            <YAxis
              width={70}
              tickFormatter={(v) => Number(v).toLocaleString()}
              tick={{ fontSize: 12 }}
              allowDecimals={false}
            />
            <Tooltip formatter={(v) => Number(v).toLocaleString() + "개"} />
            <Legend />
            <Bar dataKey="totalQty" name="수량" />
          </BarChart>
        </ResponsiveContainer>
      </div>

      {/* 하단 간단 라벨 */}
      <div style={{ padding: "6px 10px", fontSize: 12, color: "#666" }}>
        {month ? `${month} 기준 Top ${topN}` : "월 선택"}
      </div>

      {loadingItems && (
        <div className="p-2 text-sm text-gray-500">선택 월 데이터 로딩중…</div>
      )}
      {!loadingItems && !chartData.length && (
        <div className="p-2 text-sm text-gray-500">
          선택한 월에 데이터가 없습니다.
        </div>
      )}
    </div>
  );
}
