import ForecastWithSelector from "../components/ForecastWithSelector";
import MonthlySalesChart from "../components/MonthlySalesChart";
import ItemQtyMonthlyChart from "../components/ItemQtyMonthlyChart";


const WoodenMainPage = () => {
  return (
    
    <div className="gridbox">
      {/* 1. 드롭다운 + 예측 */}
      <div className="grid">
        <ForecastWithSelector defaultHorizon={12} />
      </div>

      {/* 2. 아래: 2열 그리드 (월별 매출, 상품별 월 판매 수량) */}
      <div
        className="gridbox"
        style={{
          display: "grid",
          gap: 16,
          gridTemplateColumns: "1fr 1fr",          // 2열
          gridAutoRows: "minmax(480px, auto)",     // ← 각 칸 기본 높이 (원하면 520~560으로)
          alignItems: "stretch",
        }}>

        <div className="grid" style={{ height: "100%" }}>
          <MonthlySalesChart />
        </div>

        <div className="grid" style={{ height: "100%" }}>
          <ItemQtyMonthlyChart topN={10} />
        </div>
      </div>


    </div>


  );
};
export default WoodenMainPage;
