import { useEffect } from "react";
import { useCRUD } from "../../hook/useCRUD";
import axios from "axios";
import { BASE_URL } from "../../api/PartOrderAPI";

const OrderListForm = ({ children }) => {
  const {
    onSubmit,
    setItemList,
    setCustomerId,
    customerId,
    customer,
    setItemListId,
    itemList,
    orderQty,
    setOrderQty,
    onChange,
    setCustomer,
    formData,
    setFormData,
  } = useCRUD({
    keyField: "orderNo",
  });

  useEffect(() => {
    // 고객 목록
    axios.get(`${BASE_URL}/order/sellercustomer`).then((res) => {
      const options = res.data.map((temp) => ({
        label: temp.cusComp,
        value: temp.cusNo,
      }));
      setCustomer(options);
    });

    // 상품 목록 (단가 포함)
    axios.get(`${BASE_URL}/plan/itemlist`).then((res) => {
      const options = res.data.map((temp) => ({
        label: temp.itemName,
        value: temp.itemNo,
        price: temp.itemPrice, // ✅ 단가 포함
      }));
      setItemList(options);
    });
  }, []);

  // ✅ 상품 선택 시 단가 자동 세팅
  const handleItemChange = (e) => {
    const id = Number(e.target.value);
    setItemListId(id);

    const selected = itemList.find((item) => item.value === id);
    if (selected) {
      setFormData((prev) => ({
        ...prev,
        itemId: selected.value,
        orderPrice: selected.price, // ✅ 단가 자동 반영
      }));
    }
  };

  return (
    <div className="form-wrapper">
      <form onSubmit={onSubmit} className="order-form">
        <p>
          <label>주문일자</label>
          <input
            type="date"
            name="orderDate"
            value={formData.orderDate || ""}
            onChange={onChange}
            required
          />
        </p>

        <p>
          <label>거래처</label>
          <select
            onChange={(e) =>
              setCustomerId(e.target.value ? Number(e.target.value) : null)
            }
            required
            value={customerId ?? ""}
          >
            <option value="" disabled>
              거래처를 선택하세요
            </option>
            {customer.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </p>

        <p>
          <label>상품</label>
          <select onChange={handleItemChange} required>
            <option value="" disabled>
              상품을 선택하세요
            </option>
            {itemList.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </p>

        <p>
          <label>수량</label>
          <input
            type="number"
            min="1"
            name="orderQty"
            value={orderQty || ""}
            onChange={(e) => setOrderQty(Number(e.target.value))}
            required
          />
        </p>

        <p>
          <label>단가</label>
          <input
            type="number"
            name="orderPrice"
            value={formData.orderPrice || ""}
            readOnly // ✅ 자동 반영되므로 수정 불가
          />
        </p>

        <p>
          <label>주문상태</label>
          <input
            type="text"
            name="orderState"
            value={formData.orderState || ""}
            onChange={onChange}
          />
        </p>

        <p>
          <label>납품상태</label>
          <input
            type="text"
            name="orderDeliState"
            value={formData.orderDeliState || ""}
            onChange={onChange}
          />
        </p>

        <p>
          <label>납품일자</label>
          <input
            type="date"
            name="deliveryDate"
            value={formData.deliveryDate || ""}
            onChange={onChange}
            required
          />
        </p>

        <p>
          <label>배송주소</label>
          <input
            type="text"
            name="cusAddr"
            value={formData.cusAddr || ""}
            onChange={onChange}
          />
        </p>

        {children}
      </form>
    </div>
  );
};

export default OrderListForm;
