// src/form/order/OrderListForm.jsx
const OrderListForm = ({
  customer,
  setCustomerId,
  customerId,
  itemList,
  setItemListId,
  orderQty,
  setOrderQty,
  handleChange,
  children,
}) => {
  return (
    <div className="form-wrapper">
      <form>
        {/* 거래처 선택 */}
        <p>
          <label>거래처</label>
          <select
            onChange={(e) =>
              setCustomerId(e.target.value ? Number(e.target.value) : null)
            }
            required
            value={customerId ?? ""}
          >
            <option value="" disabled>
              거래처를 선택하세요
            </option>
            {customer.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </p>

        {/* 상품 선택 */}
        <p>
          <label>상품</label>
          <select
            onChange={(e) => setItemListId(Number(e.target.value))}
            required
          >
            <option value="" disabled>
              상품을 선택하세요
            </option>
            {itemList.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </p>

        {/* 수량 입력 */}
        <label>수량</label>
        <input
          type="number"
          min="1"
          value={orderQty}
          onChange={(e) => setOrderQty(Number(e.target.value))}
          required
        />

        {/* 나머지 입력 필드 */}
        <label>주문상태</label>
        <input type="text" name="orderState" onChange={handleChange} />

        <label>납품상태</label>
        <input type="text" name="orderDeliState" onChange={handleChange} />

        <label>배송주소</label>
        <input type="text" name="cusAddr" onChange={handleChange} />

        {children}
      </form>
    </div>
  );
};

export default OrderListForm;
