import { useEffect, useState } from "react";
import { fetchMonthlySalesAll } from "../api/salesApi";
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from "recharts";

const MonthlySalesChart = () => {
  const [rows, setRows] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    (async () => {
      try {
        const data = await fetchMonthlySalesAll(); // [{ ym, total }, ...]
        setRows(Array.isArray(data) ? data : []);
      } catch (e) {
        console.error("fetchMonthlySalesAll error:", e);
      } finally {
        setLoading(false);
      }
    })();
  }, []);

  if (loading) return <div className="card">월별 매출 로딩중...</div>;
  if (!rows.length)
    return <div className="card">월별 매출 데이터가 없습니다.</div>;

  return (
    <div className="card" style={{ height: "100%" }}>
      <div className="card-header">
        <h3 className="card-title">월별 매출</h3>
      </div>

      <div className="chart-body">
        <ResponsiveContainer width="100%" height="100%">
          <LineChart
            key={rows.length} // ⬅️ 추가
            data={rows}
            margin={{ top: 12, right: 16, left: 84, bottom: 18 }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis
              dataKey="ym"
              tick={{ fontSize: 12 }}
              angle={-30}
              height={64}
              tickMargin={8}
            />
            {/* Y축: 폭 확보 + 천단위 콤마 → 앞자리 숫자 보임 */}
            <YAxis
              width={84}
              tickFormatter={(v) => Number(v).toLocaleString()}
              tick={{ fontSize: 12 }}
              allowDecimals={false}
            />
            <Tooltip formatter={(v) => Number(v).toLocaleString() + "원"} />
            <Legend />
            <Line
              type="monotone"
              dataKey="total"
              stroke="#8884d8"
              name="매출액"
              strokeWidth={2}
            />
          </LineChart>
        </ResponsiveContainer>
      </div>

      {/* 하단 간단 라벨 */}
      <div style={{ padding: "6px 10px", fontSize: 12, color: "#666" }}>
        월 단위 추이 (단위: 원)
      </div>
    </div>
  );
};

export default MonthlySalesChart;
