import { useEffect, useMemo, useState } from "react";
import { useNavigate } from "react-router-dom";
import BackButtonComponent from "../../components/BackButtonComponent.jsx";
import ButtonComponent from "../../components/ButtonComponent.jsx";
import { getItemStockList } from "../../api/ItemStockAPI.jsx";
import { ItemStockColumns } from "../../arrays/ItemStockArrays.jsx";

const StockListPage = () => {
  const navigate = useNavigate();
  const [rows, setRows] = useState([]);
  const [q, setQ] = useState("");

  const reload = async () => {
    try {
      const list = await getItemStockList();
      setRows(Array.isArray(list) ? list : []);
    } catch (e) {
      console.error(e);
      setRows([]);
      alert(e?.response?.data?.error || "재고 목록을 가져오지 못했습니다.");
    }
  };

  useEffect(() => { reload(); }, []);

  const filtered = useMemo(() => {
    const term = q.trim().toLowerCase();
    if (!term) return rows;
    return rows.filter(r => (r.itemName || "").toLowerCase().includes(term));
  }, [rows, q]);

  const totals = useMemo(() => {
    return filtered.reduce(
      (acc, r) => {
        acc.isQty += Number(r.isQty || 0);
        acc.totalIn += Number(r.totalIn || 0);
        acc.totalOut += Number(r.totalOut || 0);
        return acc;
      },
      { isQty: 0, totalIn: 0, totalOut: 0 }
    );
  }, [filtered]);

  return (
    <div className="page-wrapper">
      <BackButtonComponent text="< 이전페이지" onClick={() => navigate(-1)} />
      <h2 style={{ textAlign: "center" }}>생산 재고</h2>

      {/* 상단 툴바 */}
      <div style={{ display: "flex", gap: 12, alignItems: "center", margin: "8px 0" }}>
        <input
          value={q}
          onChange={(e) => setQ(e.target.value)}
          placeholder="상품명 검색"
          style={{ flex: "0 1 240px" }}
        />
        <ButtonComponent onClick={reload} text="새로고침" cln="submit" />
        <div style={{ marginLeft: "auto", fontWeight: 600 }}>
          현재재고 합: {totals.isQty.toLocaleString()} &nbsp;/&nbsp;
          누적입고: {totals.totalIn.toLocaleString()} &nbsp;/&nbsp;
          누적출고: {totals.totalOut.toLocaleString()}
        </div>
      </div>

      {/* 표 */}
      <table>
        <thead>
          <tr>
            {ItemStockColumns.map((c) => (
              <th key={c.id}>{c.label}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {filtered.length ? (
            filtered.map((row) => (
              <tr key={row.isNo} className="row">
                {ItemStockColumns.map((c) => (
                  <td key={`${row.isNo}-${c.id}`} style={{ textAlign: c.align || "left" }}>
                    {typeof row[c.key] === "number"
                      ? Number(row[c.key]).toLocaleString()
                      : (row[c.key] ?? "")}
                  </td>
                ))}
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan={ItemStockColumns.length} style={{ textAlign: "center" }}>
                데이터가 없습니다.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
};

export default StockListPage;
