import { BASE_URL } from "./PartOrderAPI";
import axios_api from "./axios";

const host = BASE_URL;

// 전체 주문 조회
export const getItemList = async () => {
    const res = await axios_api.get(`${host}/plan/itemlist`);
    // console.log("API 응답:", response.data);
    return res.data;
};



export const createItemList = async (formData) => {
  const res = await axios_api.post(`${host}/plan/itemlist`, formData, {
    headers: {
      'Content-Type': 'application/json',
    },
  });
  return res.data;
};

// 단건 수정
export const updateItemList = async (formData) => {
  const res = await axios_api.put(`${host}/plan/itemlist`, formData, {
    headers: { 'Content-Type': 'application/json' },
  });
  return res.data;
};

export const deleteItemList = async (itemNo) => {
  const res = await axios_api.delete(`${host}/plan/itemlist`, { 
    data: { itemNo } 
  });
  return res.data;
};
