import { useEffect } from "react";
import axios from "axios";
import { useCRUD } from "../../hook/useCRUD";
import { BASE_URL } from "../../api/PartOrderAPI";

const OrderListForm = ({ children }) => {
  const {
    onSubmit,
    setCustomer,
    setCustomerId,
    customer,
    customerId,
    setItemList,
    setItemId,
    itemList,
    itemId,
    orderQty,
    setOrderQty,
    orderPrice,
    setOrderPrice,
    orderDate,
    setOrderDate,
    orderState,
    setOrderState,
    orderDeliState,
    setOrderDeliState,
    cusAddr,
    setCusAddr,
  } = useCRUD({
    keyField: "orderNo", // DTO에서 PK
  });

  useEffect(() => {
    console.log("요청 URL:", BASE_URL);

    // 고객 목록 불러오기
    axios.get(`${BASE_URL}/order/sellercustomer`).then((res) => {
      const options = res.data.map((temp) => ({
        label: temp.cusComp, // 회사명
        value: temp.cusNo,   // PK
      }));
      setCustomer(options);
    });

    // 상품 목록 불러오기
    axios.get(`${BASE_URL}/plan/itemlist`).then((res) => {
      const options = res.data.map((temp) => ({
        label: temp.itemName, // 상품명
        value: temp.itemNo,   // PK
        price: temp.itemPrice, // 단가
      }));
      setItemList(options);
    });
  }, []);

  // 상품 선택 시 단가 자동 입력
  const handleItemChange = (e) => {
    const selectedId = Number(e.target.value);
    setItemId(selectedId);

    const selectedItem = itemList.find((i) => i.value === selectedId);
    if (selectedItem) {
      setOrderPrice(selectedItem.price); // 자동 단가 입력
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const payload = {
      customerId,
      itemId,
      orderQty,
      orderPrice,
      orderState,
      orderDeliState,
      orderDate,
      cusAddr,
    };
    console.log("전송 데이터:", payload);
    onSubmit(payload); // useCRUD 훅에서 처리
  };

  return (
    <div className="form-wrapper">
      <form onSubmit={handleSubmit}>
        {/* 거래처 */}
        <p>
          <label>거래처</label>
          <select
            onChange={(e) =>
              setCustomerId(e.target.value ? Number(e.target.value) : null)
            }
            required
            value={customerId ?? ""}
          >
            <option value="" disabled>
              거래처를 선택하세요
            </option>
            {customer.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </p>

        {/* 상품 */}
        <p>
          <label>상품</label>
          <select onChange={handleItemChange} required value={itemId ?? ""}>
            <option value="" disabled>
              상품을 선택하세요
            </option>
            {itemList.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </p>

        {/* 수량 */}
        <p>
          <label>수량</label>
          <input
            type="number"
            min="1"
            value={orderQty}
            onChange={(e) => setOrderQty(Number(e.target.value))}
            required
          />
        </p>

        {/* 단가 (자동 세팅) */}
        <p>
          <label>단가</label>
          <input
            type="number"
            value={orderPrice ?? ""}
            onChange={(e) => setOrderPrice(Number(e.target.value))}
            readOnly
          />
        </p>

        {/* 주문일자 */}
        <p>
          <label>주문일자</label>
          <input
            type="date"
            value={orderDate || ""}
            onChange={(e) => setOrderDate(e.target.value)}
            required
          />
        </p>

        {/* 주문상태 */}
        <p>
          <label>주문상태</label>
          <input
            type="text"
            value={orderState || ""}
            onChange={(e) => setOrderState(e.target.value)}
          />
        </p>

        {/* 납품상태 */}
        <p>
          <label>납품상태</label>
          <input
            type="text"
            value={orderDeliState || ""}
            onChange={(e) => setOrderDeliState(e.target.value)}
          />
        </p>

        {/* 배송주소 */}
        <p>
          <label>배송주소</label>
          <input
            type="text"
            value={cusAddr || ""}
            onChange={(e) => setCusAddr(e.target.value)}
          />
        </p>

        {children}
      </form>
    </div>
  );
};

export default OrderListForm;
