import { useEffect } from "react";
import { OrderListArr } from "../../arrays/OrderListArrays";
import axios from "axios";
import { useCRUD } from "../../hook/useCRUD";
import { BASE_URL } from "../../api/PartOrderAPI";

const OrderListForm = ({ children }) => {
  const {
    onSubmit,
    setItemList,
    setCustomerId,
    customerId,
    customer,
    setItemListId,
    itemList,
    orderQty,
    setOrderQty,
    onChange,
    setCustomer,
  } = useCRUD({
    keyField: "orderNo",
  });

  useEffect(() => {
    console.log("요청 URL:", BASE_URL);

    // 고객 목록 불러오기
    axios.get(`${BASE_URL}/order/sellercustomer`).then((res) => {
      const options = res.data.map((temp) => ({
        label: temp.cusComp, // 고객사명
        value: temp.cusNo,   // PK
      }));
      setCustomer(options);
    });

    // 상품 목록 불러오기
    axios.get(`${BASE_URL}/plan/itemlist`).then((res) => {
      const options = res.data.map((temp) => ({
        label: temp.itemName, // 상품명
        value: temp.itemNo,   // PK (중요!)
      }));
      setItemList(options);
    });
  }, []); // 최초 렌더링 시 한 번만 실행

  return (
    <div className="form-wrapper">
      <form onSubmit={onSubmit}>
        <p>
          <label>거래처</label>
          <select
            onChange={(e) =>
              setCustomerId(e.target.value ? Number(e.target.value) : null)
            }
            required
            value={customerId ?? ""}
          >
            <option value="" disabled>
              거래처를 선택하세요
            </option>
            {customer.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </p>

        <p>
          <label>상품</label>
          <select
            onChange={(e) => setItemListId(e.target.value ? Number(e.target.value) : null)}
            required
          >
            <option value="" disabled>
              상품을 선택하세요
            </option>
            {itemList.map((option) => (
              <option key={option.value} value={option.value}>
                {option.label}
              </option>
            ))}
          </select>
        </p>

        <label>수량</label>
        <input
          type="number"
          min="1"
          value={orderQty}
          onChange={(e) => setOrderQty(Number(e.target.value))}
          required
        />

        {OrderListArr.slice(3).map((data) => (
          <p key={data.id}>
            <span className="form-child">{data.content}</span>
            <input type="text" onChange={onChange} />
          </p>
        ))}

        {children}
      </form>
    </div>
  );
};

export default OrderListForm;
