import { OrderListArr } from "../../arrays/OrderListArrays";

const OrderListForm = ({
  formData,
  onChange,
  customer,
  itemList,
  setCustomerId,
  setItemListId,
  children,
}) => {
  
  // ÏÉÅÌíà ÏÑ†ÌÉù Ïãú Îã®Í∞Ä ÏûêÎèô ÏûÖÎ†•
  const handleItemChange = (e) => {
    const itemId = Number(e.target.value);
    const selectedItem = itemList.find((i) => i.value === itemId);

    if (selectedItem) {
      setItemListId(itemId);
      // itemId ÏóÖÎç∞Ïù¥Ìä∏
      onChange({ target: { name: "itemId", value: itemId } });
      // Îã®Í∞Ä(orderPrice) ÏûêÎèô ÏûÖÎ†•
      onChange({ target: { name: "orderPrice", value: selectedItem.price } });
    }
  };
  // Ï¥ù Í∏àÏï°
  const computedTotal =
    Number(formData.orderQty || 0) * Number(formData.orderPrice || 0);

  return (
    <div className="form-wrapper">
      {OrderListArr.slice(1).map((data) => (
        <div className="form-content" key={data.id}>
          <p>
            <span className="form-child">{data.content}</span>

            {/* üîΩ Í±∞ÎûòÏ≤ò Select */}
            {data.clmn === "cusComp" ? (
              <select
                name="customerId"
                value={formData.customerId || ""}
                onChange={(e) => {
                  onChange({ target: { name: "customerId", value: e.target.value } });
                  setCustomerId(Number(e.target.value));
                }}
                required
              >
                <option value="">Í±∞ÎûòÏ≤ò ÏÑ†ÌÉù</option>
                {customer.map((c) => (
                  <option key={c.value} value={c.value}>
                    {c.label}
                  </option>
                ))}
              </select>
            ) : data.clmn === "itemName" ? (
              /* üîΩ ÏÉÅÌíà Select */
              <select
                name="itemId"
                value={formData.itemId || ""}
                onChange={handleItemChange}
                required
              >
                <option value="">ÏÉÅÌíà ÏÑ†ÌÉù</option>
                {itemList.map((i) => (
                  <option key={i.value} value={i.value}>
                    {i.label}
                  </option>
                ))}
              </select>
            ) : data.clmn === "orderState" ? (
              /* üîΩ Ï£ºÎ¨∏ÏÉÅÌÉú Select */
              <select
                name="orderState"
                value={formData.orderState || ""}
                onChange={onChange}
              >
                <option value="ÏäπÏù∏ÎåÄÍ∏∞">ÏäπÏù∏ÎåÄÍ∏∞</option>
                <option value="ÏäπÏù∏ÏôÑÎ£å">ÏäπÏù∏ÏôÑÎ£å</option>
              </select>
            ) : data.clmn === "orderDeliState" ? (
              /* üîΩ ÎÇ©ÌíàÏÉÅÌÉú Select */
              <select
                name="orderDeliState"
                value={formData.orderDeliState || ""}
                onChange={onChange}
              >
                <option value="ÎÇ©ÌíàÎåÄÍ∏∞">ÎÇ©ÌíàÎåÄÍ∏∞</option>
                <option value="ÎÇ©ÌíàÏôÑÎ£å">ÎÇ©ÌíàÏôÑÎ£å</option>
              </select>
            ) : data.clmn === "totalPrice" ? (
              // Ï¥ù Í∏àÏï° = ÏàòÎüâ X Îã®Í∞Ä (ÌôîÎ©¥Îã® ÌëúÏãúÎßå)
              <input
                name="totalPrice"
                readOnly
                value={String(computedTotal)}
              />
            ) : (
              /* üîΩ Í∏∞Î≥∏ input */
              <input
                type={data.type || "text"}
                name={data.clmn}
                value={formData[data.clmn] || ""}
                onChange={onChange}
                readOnly={data.clmn === "orderPrice"} // ‚úÖ Îã®Í∞Ä ÏûêÎèô ÏûÖÎ†•
              />
            )}
          </p>
        </div>
      ))}

      {children}
    </div>
  );
};

export default OrderListForm;
