import { OrderListArr } from "../../arrays/OrderListArrays";

const OrderListForm = ({
  formData,
  onChange,
  customer,
  itemList,
  setCustomerId,
  setItemListId,
  children,
}) => {
  return (
    <div className="form-wrapper">
      {OrderListArr.slice(1).map((data) => (
        <div className="form-content" key={data.id}>
          <p>
            <span className="form-child">{data.content}</span>

            {/* üîΩ ÌäπÏàò input Ï≤òÎ¶¨ */}
            {data.clmn === "cusComp" ? (
              <select
                name="cusComp"
                value={formData.customerId || ""}
                onChange={(e) => {
                  onChange(e);
                  setCustomerId(Number(e.target.value));
                }}
                required
              >
                <option value="">Í±∞ÎûòÏ≤ò ÏÑ†ÌÉù</option>
                {customer.map((c) => (
                  <option key={c.value} value={c.value}>
                    {c.label}
                  </option>
                ))}
              </select>
            ) : data.clmn === "itemName" ? (
              <select
                name="itemName"
                value={formData.itemId || ""}
                onChange={(e) => {
                  onChange(e);
                  setItemListId(Number(e.target.value));
                }}
                required
              >
                <option value="">ÏÉÅÌíà ÏÑ†ÌÉù</option>
                {itemList.map((i) => (
                  <option key={i.value} value={i.value}>
                    {i.label}
                  </option>
                ))}
              </select>
            ) : (
              <input
                type={data.type}
                name={data.clmn}
                value={formData[data.clmn] || ""}
                onChange={onChange}
              />
            )}
          </p>
        </div>
      ))}

      {children}
    </div>
  );
};

export default OrderListForm;
