import { Outlet,useLocation,useNavigate,Link } from "react-router-dom";
import { useState,useEffect } from "react";
import { NavBar } from "../arrays/MainArrays";
import AsideComponent from "../components/AsideComponent";


const BasicLayout = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    const activeNavMap = {
        "/order/orderlist": 1,
        "/order/sellercustomer":1,
        "/order/orderreceive":1,
        "/buyer/partlist": 2,
        "/buyer/buyercustomer": 2,
        "/buyer/partorder":2,
        "/buyer/buyerdelivery":2,
        "/plan/itemlist":3,
        "/plan/planlist": 3,
        "/plan/bom": 3,
        "/stock/stocklist": 4,
        "/stock/sellamount":4,
    };

    const asideGroups = {
        order: "/order",
        buyer: "/buyer",
        plan: "/plan",
        stock: "/stock"
    };
    const currentNavID = activeNavMap[location.pathname];

    const orderAside = location.pathname.startsWith(asideGroups.order);

    const buyerAside = location.pathname.startsWith(asideGroups.buyer);

    const planAside = location.pathname.startsWith(asideGroups.plan);

    const stockAside = location.pathname.startsWith(asideGroups.stock);

    function navigateAndReset(path){
        setAsideID(null);
        setNavID(null);
        navigate(path);
    }

    function handleNavClick(list){
        setNavID(list.id);
    }

    useEffect(() => {
    const loginStatus = localStorage.getItem("isLoggedIn");
    setIsLoggedIn(loginStatus === "true");

    // 상단 탭 ID는 1/2/3/4 그룹 기준 설정
    if (location.pathname.startsWith("/order")) setNavID(1);
    else if (location.pathname.startsWith("/buyer")) setNavID(2);
    else if (location.pathname.startsWith("/plan")) setNavID(3);
    else if (location.pathname.startsWith("/stock")) setNavID(4);
    else setNavID(null);
    }, [location.pathname]);

    return(
        <>
            <header>
                <div className="logo">
                    <h1>
                        <Link to = "/">
                            WOODEN LOGO
                        </Link>
                    </h1>
                </div>
                <nav>
                    <ul className="header-ul">
                        { NavBar.map(data => (
                            <li key={data.id} className={currentNavID === data.id ? "selected" : ""}>
                                <Link
                                    to={isLoggedIn ? data.path : "/login"}
                                    className="nav-items"
                                    onClick={(e) => {
                                        if (!isLoggedIn) {
                                            e.preventDefault();
                                            alert("로그인이 필요합니다");
                                            navigateAndReset("/login");
                                        } else {
                                            handleNavClick(data);
                                        }
                                    }}>

                                    {data.name}                                
                                </Link>
                            </li>                            
                        ))}
                    </ul>
                    <p className="loginbtn">    {/*테스트*/}
                    {isLoggedIn ? 
                        <span
                        onClick={() => {
                        localStorage.removeItem("isLoggedIn");
                        setIsLoggedIn(false);
                        alert("로그아웃 되었습니다.")
                        navigateAndReset("/");
                        }}
                        >
                            로그아웃
                        </span>
                    : 
                        <span onClick={() => navigateAndReset("/login")}>로그인</span>
                    }
                    </p>

                </nav>
        </header>
        <div className="main_content">
        <div className = "main_aside">
            <div className="main_article">
                <ul className="aside-ul">
                    <AsideComponent
                    orderAside={orderAside} 
                    buyerAside={buyerAside}
                    planAside={planAside}
                    stockAside={stockAside}
                    />
                </ul>

            </div>
        </div>
        <div className="main_body">
            <Outlet/>
        </div>
        </div>

        </>
    )
}
export default BasicLayout;
