import axios from "axios";
import { BASE_URL } from "./PartOrderAPI";

const host = BASE_URL;

// 전체 주문 조회
export const getOrderList = async () => {
    const res = await axios.get(`${host}/order/orderlist`);
    // console.log("API 응답:", response.data);
    return res.data;
};



export const createOrderList = async (formData) => {
  const res = await axios.post(`${host}/order/orderlist`, formData, {
    headers: {
      'Content-Type': 'application/json',
    },
  });
  return res.data;
};

// 단건 수정
export const updateOrderList = async (formData) => {
  const res = await axios.put(`${host}/order/orderlist`, formData, {
    headers: { 'Content-Type': 'application/json' },
  });
  return res.data;
};

export const deleteOrderList = async (itemNo) => {
  const res = await axios.delete(`${host}/order/orderlist`, { 
    data: { itemNo } 
  });
  return res.data;
};
