// src/api/salesApi.js
// axios 공용 인스턴스(팀 공용) 그대로 사용
import axios_api from "./axios";

/**
 * 월별 매출
 * GET /api/sales/monthly
 */
export async function fetchMonthlySalesAll() {
  const res = await axios_api.get("/sales/monthly");
  const rows = Array.isArray(res.data) ? res.data : res.data?.rows ?? [];
  return rows;
}

/**
 * 특정 월 Top10 (서버에서 집계)
 * GET /api/sales/item-qty-monthly?ym=YYYY-MM
 */
export async function fetchItemQtyMonthlyByYm(ym) {
  const res = await axios_api.get(`/sales/item-qty-monthly`, {
    params: { ym },
  });
  const rows = Array.isArray(res.data) ? res.data : res.data?.rows ?? [];
  return rows;
}

/**
 * 모든 월×상품 수량 (드롭다운/차트용 전체 데이터)
 * GET /api/sales/item-qty-monthly-all
 *  - SalesRepository.findItemQtyMonthlyAll() 반환 스키마에 맞춤
 *    { ym, itemNo, itemName, totalQty }
 */
export async function fetchItemQtyMonthlyAll() {
  const res = await axios_api.get("/sales/item-qty-monthly-all");
  const rows = Array.isArray(res.data) ? res.data : res.data?.rows ?? [];
  return rows;
}
